    inviaOrdine() {
      const boundary = '--__X_PAW_BOUNDARY__\n';
      const header = `Content-Disposition: form-data; name="file"; filename="test.dat"\n Content-Type: application/octet-stream\n\n`;
      const footer = '\n--__X_PAW_BOUNDARY__';

      // Crea il contenuto finale del file
      const fileContent = boundary + header + this.textContent + footer;
      // Crea un blob con i dati
      const blob = new Blob([fileContent], {
        type: 'text/plain'
      });

      // Crea un link per il download
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'file.dat'; // Nome del file da scaricare

      // Simula un click sul link per avviare il download
      document.body.appendChild(a);
      a.click();

      // Rimuovi il link dal DOM
      document.body.removeChild(a);
      URL.revokeObjectURL(url); // Libera la memoria
    }

